import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "./NavBar.css";

/*GLUPO JE DA IMAS POSEBAN ROUT ZA SVAKU KATEGORIJU JER JE CE PAGE BITI ISTI JEDINA RAZLIKA CE BITI ITEMI*/ 
//DAKLE ZA MENU CLICK RENDERUJES KOMPONENTU SA PARAMATERIMA ZAVISNO OD KATEGORIJA
//HOME BUTTON?
/* Expandovane kategorije izizskuju posebnu kostimizaciju*/

const MutualCategs = ({linkExt}) => {

const b =`/${linkExt}/sandals`, a= `/${linkExt}/sneakers`, c=`/${linkExt}/boots`, d=`/${linkExt}/slippers`, e=`/${linkExt}/shoes`;


  return(
    <>
    <li className="nav-item">
    <NavLink className="nav-links"  exact to={a}>
  Sneakers
  </NavLink>
    </li>
    <li className="nav-item">
    <NavLink className="nav-links"  exact to={b}>
  Sandals
  </NavLink>
    </li>
    <li className="nav-item">
    <NavLink className="nav-links"  exact to={c}>
  Boots
  </NavLink>
    </li>
    <li className="nav-item">
    <NavLink className="nav-links"  exact to={d}>
  Slippers
  </NavLink>
    </li>
    <li className="nav-item">
    <NavLink className="nav-links"  exact to={e}>
  Shoes
  </NavLink>
    </li>
    <li className={linkExt === "women"? "nav-item":"Nestani"}>
        <NavLink className="nav-links"  exact to="/women/heels">
      Heels
      </NavLink>
        </li>


    </>
  )
}



function NavBar() {
  const [click, setClick] = useState(false);
  const [showInp, setShowInp] = useState(false)
  const [search, setSearch] = useState('')
  const [expMan, setExpMan] = useState(false)
  const [expWomen, setExpWomen] = useState(false)
  const [expKids, setExpKids] = useState(false)
  const [expSale, setExpSale] = useState(false)
  const toggleMenu = () => {setClick(!click)
   setShowInp(false)}
  const toggleShowInp = () => {setShowInp(!showInp)
   setClick(false)}
//Dalje search treba sa useEffectom da spika sa serverom pogledati countries projekat iz part1
function expand(str) {
 var midija = window.matchMedia("(max-width: 960px)")
  if(midija.matches){
  switch (str){
    case 'man':
    setExpMan(!expMan)
    break;
    case 'women':
     setExpWomen(!expWomen)
     break
     case 'kids':
      setExpKids(!expKids)
      break
      case 'sale':
        setExpSale(!expSale)
        break;
  }
} else return

}
  function searchItems(e)
  { e.preventDefault();
     setSearch(e.target.value);
     }
     var midija = window.matchMedia("(max-width: 960px)")
     function mediaFunc (inFlex) {
      if(inFlex){
        if(!midija.matches)
        {
          return ( <div> <input type='search' name="q" onChange={searchItems} value={search} id="mobileSearch" className="fas fa-times" placeholder='  &#xf002; Search...'></input>  </div>)
        }
        else 
        {
          return
        }
      }
      else {
        if(midija.matches)
{  return ( <div> <input type='search' name="q" onChange={searchItems} value={search} id="mobileSearch" className={showInp?"fas fa-times": "Nestani"} placeholder='  &#xf002; Search...'></input>  </div>)
}
else 
{
  return
}
      }

     }
  return (
    <div>
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"></link>
      <nav className="navbar">
        <div className="nav-container">
          <NavLink exact to="/" className="nav-logo">
            {/*MOGAO SI DA UBACIS U NEKI KONTEJNER PA DA SKURBLAS STVAR AL MISLIM DA NEMA POTREBE*/}
            <div style={{fontSize:'1rem'}}>
            Gesix 
            <i className='fas fa-shoe-prints' style={{fontSize:'1.5rem', display:'inline', position: 'absolute', top:28, left: 80}}></i>
            <br></br>
            ObuÄ‡a
            </div>

          </NavLink>

          <ul className={click ? "nav-menu active" : "nav-menu"}>
          <div className="parent">
                      <li className="nav-item">
              <NavLink
                exact
                to="/about"
                activeClassName="active"
                className="nav-links"
                onClick={()=>expand('man') }
              >
                Man <div className='arrowheads'> &#x2304; </div> 
              </NavLink>
            </li>
            <div style={{display: expMan ? "block":"none"}}>
           <MutualCategs linkExt={'man'}/>
            </div>
            <div className="dropdown">
            <MutualCategs linkExt={'man'} />
            </div>
            </div>
            <div className="parent">
            <li className="nav-item">
              <NavLink
                exact
                to="/about"
                activeClassName="active"
                className="nav-links"
                onClick={()=>expand('women') }
              >
                Women <div className='arrowheads'> &#x2304; </div> 
              </NavLink>
            </li>
            <div style={{display: expWomen ? "block":"none"}}>
           <MutualCategs linkExt={'women'}/>
            </div>
            <div className="dropdown">
            <MutualCategs linkExt={'women'}/>
            </div>
            </div>
            <div className="parent">
            <li className="nav-item">
              <NavLink

                activeClassName="active"
                className="nav-links"
                onClick={() => expand('kids')}
              >
                Kids   <div className='arrowheads'> &#x2304; </div> 
              </NavLink>
            </li>
            <div style={{display: expKids ? "block":"none"}}>
           <MutualCategs linkExt={'kids'}/>
            </div>
   <div className="dropdown">              
            <MutualCategs linkExt={'kids'}/>
